<script setup>
import { reactive, watch } from 'vue'

let signosVitales = reactive({
    estatura: null,
    peso: null,
    temperatura:null,
    fc:null,
    fr:null,
    presionArterial:null,
    saturacionOxigeno:null,
    indiceCinturaCadera:null,
    imc:null
})
const emit = defineEmits(['signos'])

watch(signosVitales, (nuevoValor) => {
